  price: number;
  originalPrice?: number;
  imageUrl: string;
  category: string;
  retailer: string;
  productUrl: string;
  inStock: boolean;
  colors?: string[];
  sizes?: string[];
  description?: string;
}

export default function RetailProducts() {
